import React from "react";

import Card from "./Card.js";
import Indecision from "../assets/images/Indecision.png";
import clone from "../assets/images/clone.png";
import Mroom from "../assets/images/Mroom.jpg";

class Carousel extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 0,
          title: "Indecision App",
          subtitle: "The Decision Control App",
          imgSrc: Indecision,
          link: "www.github.com",
          selected: false,
        },
        {
          id: 0,
          title: "U",
          subtitle: "A Clone For Udemy Website",
          imgSrc: clone,
          link: "www.github.com",
          selected: false,
        },
        {
          id: 0,
          title: "My MRoom",
          subtitle: "The My MRoom Webpage",
          imgSrc: Mroom,
          link: "www.github.com",
          selected: false,
        },
      ],
    };
  }

  handelCardClick = (id, card) => {
    let items = [...this.state.items];

    items[id].selected = items[id].selected ? false : true;
    items.forEach((item) => {
      if (item.id !== id) {
        item.selected = false;
      }
    });
    this.setState({
      items,
    });
  };

  makeItem = (items) => {
    return items.map((item) => {
      return (
        <Card
          item={item}
          onClick={(e) => this.handelCardClick(item.id, e)}
          key={item.is}
        />
      );
    });
  };

  render() {
    return <p>Carousel works</p>;
  }
}
export default Carousel;
